import UIKit

final class OnbordingRouter: OnbordingWireframeProtocol {
    weak var viewController: UIViewController?
    
    static func createModule() -> UIViewController {
        // Change to get view from storyboard if not using progammatic UI
        let view = OnbordingViewController()
        let interactor = OnbordingInteractor()
        let router = OnbordingRouter()
        let presenter = OnbordingPresenter(interface: view, interactor: interactor, router: router)
        
        view.presenter = presenter
        interactor.presenter = presenter
        router.viewController = view
        
        return view
    }
}
